int evm_init_key(void)
{
    struct key *evm_key;
    struct encrypted_key_payload *ekp;
    int rc = 0;

    evm_key = request_key(&key_type_encrypted, EVMKEY, NULL);
    if (IS_ERR(evm_key))
        return -ENOENT;

    down_read(&evm_key->sem);
    ekp = evm_key->payload.data;
